mapper.select:
select *
from (select ROWNUM AS RN ,c.*
      from(
              select *
              from TBL_COMMUNITY
              where USER_ID=#{userId} and author = #{myName}
              order by COMMUNITY_REGISTER_DATE DESC,COMMUNITY_ID desc) c
      where ROWNUM <= #{endRow})
where RN > #{startRow}

select count(*)
from TBL_COMMUNITY
where USER_ID=#{userId} and author = #{myName}

select *
from (select ROWNUM AS RN ,c.*
      from(
              select *
              from TBL_Comment
              where USER_ID=#{userId} and author = #{myName}
              order by Comment_REGISTER_DATE DESC,Comment_ID desc) c
      where ROWNUM <= #{endRow})
where RN > #{startRow}

select count(*)
from TBL_Comment
where USER_ID=#{userId} and author = #{myName}

select count(*)
from TBL_PRO_REVIEW
where USER_ID=#{userId}

select count(*)
from TBL_USER_REVIEW
where USER_ID=#{userId}

select count(*)
from TBL_USER_REVIEW
where PRO_ID=#{proId}

select count(*)
from TBL_PRO_REVIEW
where PRO_ID=#{proId}
