mapper.delete:
delete
from tbl_user
where user_id = #{userId}
  and user_name = #{userName}

mapper.update:
update TBL_USER
set USER_PRO_APPROVAL = 'NO'
where USER_ID = #{userId}

update tbl_user
set phone_number = #{phoneNumber},
    location_id=#{locationId},
    email        = #{email}
where user_id = #{userId}

update tbl_category_list
set category_id = #{categoryId}
where user_id = #{userId}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RN, UPLOAD.* FROM ( select TUU.USER_UPLOAD_ID, TUU.USER_ID, TUU.USER_UPLOAD_TITLE, TUU.USER_UPLOAD_DATE, TU.USER_STAR_RATE, TU.USER_NAME, ts.SERVICE_NAME from TBL_USER_UPLOAD TUU inner join TBL_USER TU on TUU.USER_ID = TU.USER_ID inner join TBL_SERVICE TS on tuu.SERVICE_ID = ts.SERVICE_ID
TUU.USER_UPLOAD_TITLE LIKE '%' || #{search} || '%'
ORDER BY USER_UPLOAD_DATE DESC ) UPLOAD WHERE ROWNUM <= #{endRow} ) WHERE rn > #{startRow}

mapper.update:
update tbl_user
set USER_CASH =#{userCash}
where USER_ID = #{userId}

update TBL_PRO_REQUEST
set PRO_REQUEST_PROGRESS = 'ING'
where PRO_REQUEST_ID = #{proRequestId}

update TBL_PRO_REQUEST
set PRO_REQUEST_PROGRESS = 'POST'
where PRO_REQUEST_ID = #{proRequestId}
